@Component({
  selector: 'app-eventos',
  templateUrl: './eventos.component.html',
  styleUrls: ['./eventos.component.scss']
})
export class EventosComponent {

  public eventos: any = [
    {
      Tema: 'Angular',
      Local: 'Recife'
     },
     {
      Tema: '.NET',
      Local: 'São Paulo'
     },
     {
      Tema: 'Delphi',
      Local: 'Rio de Janeiro'
     }
  ]

    constructor() { }
    ngOnInit(): void {

  }

}
