import { Component } from '@angular/core';

@Component({
  selector: 'app-eventos',
  templateUrl: './eventos.component.html',
  styleUrls: ['./eventos.component.scss']
})
export class EventosComponent {

  public eventos: any = [
    {
      Tema: 'Angular',
      Local: 'Recife'
     },
     {
      Tema: '.NET',
      Local: 'São Paulo'
     },
     {
      Tema: 'Delphi',
      Local: 'Rio de Janeiro'
     }
  ]

    constructor() { }
    ngOnInit(): void {

  }

}
<h1>Eventos de Components</h1>

<div *ngFor="let cont of eventos">
  <p> <b>TEMA:</b> {{eventos[2].Tema}} </p>
  <p> <b>LOCAL:</b> {{eventos[2].Local}} </p>
</div>
